    # Create .env.example\n    cat > .env.example << EOF\n# ðŸ”‘ Environment Variables for ${AGENT_NAME^} Agent\n# Copy this file to .env and fill in your actual API keys\n# Never commit .env to version control!\n\n# =============================================================================\n# TOOLHOUSE CONFIGURATION (Required)\n# =============================================================================\n\n# Your Toolhouse API key (required for CLI and SDK)\n# Get this from: https://toolhouse.ai/dashboard/api-keys\nTOOLHOUSE_API_KEY=your_toolhouse_api_key_here\n\n# =============================================================================\n# LLM PROVIDER API KEYS\n# =============================================================================\n\n# OpenAI API Key (most common)\n# Get this from: https://platform.openai.com/api-keys\n# Required for: @openai/gpt-4o, @openai/gpt-4o-mini, @openai/o1-preview, etc.\nOPENAI_API_KEY=sk-your_openai_key_here\n\n# Anthropic API Key\n# Get this from: https://console.anthropic.com/\n# Required for: @anthropic/claude-sonnet-4, @anthropic/claude-haiku-3-5, etc.\nANTHROPIC_API_KEY=sk-ant-your_anthropic_key_here\n\n# Groq API Key (High-speed inference)\n# Get this from: https://console.groq.com/keys\n# Required for: @groq/llama-3.3-70b-versatile, @groq/mixtral-8x7b-32768, etc.\nGROQ_API_KEY=gsk-your_groq_key_here\n\n# Google AI Studio API Key (Gemini)\n# Get this from: https://aistudio.google.com/app/apikey\n# Required for: @google/gemini-pro, @google/gemini-pro-vision\nGOOGLE_API_KEY=your_google_ai_key_here\n\n# =============================================================================\n# COMMON EXTERNAL SERVICE API KEYS\n# =============================================================================\n\n# Database Connections\nDATABASE_URL=postgresql://username:password@localhost:5432/database_name\nMONGO_URL=mongodb://username:password@localhost:27017/database_name\nREDIS_URL=redis://localhost:6379\n\n# Email Services\nSENDGRID_API_KEY=SG.your_sendgrid_key_here\nSENDGRID_FROM_EMAIL=noreply@yourdomain.com\n\n# Communication Services\nSLACK_BOT_TOKEN=xoxb-your_slack_bot_token_here\nSLACK_WEBHOOK_URL=https://hooks.slack.com/services/YOUR/SLACK/WEBHOOK\n\n# Web Services\nOPENWEATHER_API_KEY=your_openweather_key_here\nNEWS_API_KEY=your_news_api_key_here\n\n# Search Services\nGOOGLE_SEARCH_API_KEY=your_google_search_key_here\nGOOGLE_SEARCH_ENGINE_ID=your_search_engine_id_here\n\n# Cloud Storage (AWS)\nAWS_ACCESS_KEY_ID=your_aws_access_key_here\nAWS_SECRET_ACCESS_KEY=your_aws_secret_key_here\nAWS_REGION=us-west-2\nAWS_S3_BUCKET=your_s3_bucket_name\n\n# Development Tools\nGITHUB_TOKEN=ghp_your_github_personal_access_token_here\nNOTION_API_KEY=secret_your_notion_integration_key_here\n\n# =============================================================================\n# AGENT CONFIGURATION\n# =============================================================================\n\n# Environment settings\nENVIRONMENT=development\nDEBUG=true\nLOG_LEVEL=info\n\n# Default model configuration\nDEFAULT_MODEL=@openai/gpt-4o-mini\nMAX_TOKENS=4000\nTEMPERATURE=0.7\n\n# Agent behavior\nMAX_RETRIES=3\nTIMEOUT_SECONDS=30\n\n# =============================================================================\n# SECURITY NOTES\n# =============================================================================\n\n# ðŸ”’ IMPORTANT SECURITY REMINDERS:\n# 1. Never commit .env files to version control\n# 2. Use different API keys for development, staging, and production\n# 3. Regularly rotate your API keys\n# 4. Set appropriate permissions: chmod 600 .env\n# 5. Keep this file updated as you add new integrations\n\n# =============================================================================\n# QUICK SETUP GUIDE\n# =============================================================================\n\n# 1. Copy this file: cp .env.example .env\n# 2. Get your Toolhouse API key: https://toolhouse.ai/dashboard/api-keys\n# 3. Get your LLM provider key (OpenAI is most common): https://platform.openai.com/api-keys\n# 4. Fill in the keys above in your .env file\n# 5. Test your setup: ./scripts/test.sh\n\n# For more API keys and detailed setup, see:\n# https://github.com/Magnussmari/ultimate-toolhouse-guide/blob/main/templates/studio-to-vscode/.env.example\nEOF"